('1362', 'commander', '帥', '<img src=\"E5B8A5.png\" />', 'commander, maestro, towel', '9', '34', 'The COMMANDER is a military MAESTRO who never throws in the TOWEL.', 'JH', '1', 'スイ', '', NULL, NULL),
('1363', 'bureaucrat', '官', '<img src=\"E5AE98.png\" />', 'bureaucrat, house, maestro without baton', '8', '34', 'No one likes classical music anymore. Our Maestro is here at home without his baton sighing sadly before he heads off for another day as his new job as a bureaucrat.', '4', '3', 'カン', '', NULL, NULL),
